import React, { useState } from 'react';
import {
    Box,
    Heading,
    FormControl,
    FormLabel,
    Input,
    Button,
    useToast,
} from '@chakra-ui/react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


const FormFornitore = () => {
  const [ragioneSociale, setRagioneSociale] = useState('');
  const [indirizzo, setIndirizzo] = useState('');
  const [partitaIva, setPartitaIva] = useState('');
  const [codiceSdi, setCodiceSdi] = useState('');
  const toast = useToast();
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
           await axios.post('/api/fornitori', {
                ragione_sociale: ragioneSociale,
                indirizzo: indirizzo,
               partita_iva: partitaIva,
               codice_sdi: codiceSdi,
           });
           toast({
               title: 'Fornitore creato',
               status: 'success',
               duration: 3000,
                isClosable: true,
           });
            navigate('/'); // Navigate to dashboard after form submission
        } catch (error) {
            toast({
                title: 'Errore durante la creazione del fornitore',
                description: 'Si è verificato un errore durante la creazione del fornitore.',
               status: 'error',
                duration: 5000,
               isClosable: true,
            });
        }
    };


  return (
      <Box p={4}>
          <Heading mb={4}>Aggiungi Fornitore</Heading>
          <form onSubmit={handleSubmit}>
              <FormControl mb={4}>
                  <FormLabel>Ragione Sociale</FormLabel>
                  <Input type="text" value={ragioneSociale} onChange={(e) => setRagioneSociale(e.target.value)} required />
              </FormControl>
              <FormControl mb={4}>
                  <FormLabel>Indirizzo</FormLabel>
                  <Input type="text" value={indirizzo} onChange={(e) => setIndirizzo(e.target.value)} required />
              </FormControl>
               <FormControl mb={4}>
                  <FormLabel>Partita IVA</FormLabel>
                 <Input type="text" value={partitaIva} onChange={(e) => setPartitaIva(e.target.value)} required />
              </FormControl>
              <FormControl mb={4}>
                  <FormLabel>Codice SDI</FormLabel>
                 <Input type="text" value={codiceSdi} onChange={(e) => setCodiceSdi(e.target.value)} required />
              </FormControl>
              <Button colorScheme="teal" type="submit">Aggiungi</Button>
          </form>
      </Box>
  );
};

export default FormFornitore;
